select
, o.REMARK FROM t_employee z1 LEFT JOIN( select * from t_offline_oper where 1=1
AND YEAR = YEAR(NOW())
AND MONTH = MONTH(NOW())
AND rm_id = #{rmId}
) o ON z1.EMPLOYEE_ID = o.EMPLOYEE_ID WHERE 1=1 AND z1.RM_USER_ID = #{rmId} AND z1.RESOURCE_STATUS='Active' ORDER BY o.e_hr DESC

