mapper.select:
SELECT count(0) FROM t_candidate c WHERE c.CANDIDATE_ID IN (SELECT DISTINCT ci.CANDIDATE_ID FROM t_candidate_interview ci JOIN t_user u ON ci.INTERVIEWER_ID = u.USER_ID)
AND CANDIDATE_NAME = #{candidateName}
AND TEL = #{candidateTel}

SELECT * FROM t_candidate c WHERE c.CANDIDATE_ID IN
	(SELECT DISTINCT ci.CANDIDATE_ID 
	FROM t_candidate_interview ci
	JOIN t_user u 
	ON ci.INTERVIEWER_ID = u.USER_ID) 

	AND CANDIDATE_NAME = #{candidateName}


	AND TEL = #{candidateTel}

	LIMIT #{currentPage},#{pageDataCount};
