mapper.select:
	    SELECT u.USER_ID, u.USER_NAME, u.NICKNAME, u.USER_TYPE, u.BU, d.CS_SUBDEPT_NAME
		FROM t_user AS u
		LEFT JOIN t_cs_dept d
		ON u.CSDEPT_ID = d.CS_SUBDEPT_ID
        WHERE 1 = 1
        
			
				AND u.USER_NAME = #{eHr}
			
			
				AND u.NICKNAME = #{userName}
			
			
				AND u.USER_TYPE = #{userType}
			
			
				AND d.CS_SUBDEPT_NAME = #{du}
			
			
				AND u.BU = #{bu}
			
		LIMIT #{currentPage},10;

	    SELECT count(0)
		FROM t_user AS u
		LEFT JOIN t_cs_dept d
		ON u.CSDEPT_ID = d.CS_SUBDEPT_ID
        WHERE 1 = 1
        
			
				AND u.USER_NAME = #{eHr}
			
			
				AND u.NICKNAME = #{userName}
			
			
				AND u.USER_TYPE = #{userType}
			
			
				AND d.CS_SUBDEPT_NAME = #{du}
			
			
				AND u.BU = #{bu}
